import React, { useState } from 'react';
import axios from 'axios';
import './AddReservationForm.css';

const AddReservationForm = ({ onCreated }) => {
  const [form, setForm] = useState({
    clientId: '',
    startDate: '',
    endDate: '',
    promotionId: ''
  });

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    axios.post('http://localhost:7074/reservations', form)
      .then(() => {
        alert('Rezerwacja dodana!');
        setForm({ clientId: '', startDate: '', endDate: '', promotionId: '' });
        onCreated(); // odśwież listę
      })
      .catch(err => {
        console.error('Błąd przy tworzeniu rezerwacji:', err);
        alert('Błąd podczas zapisu.');
      });
  };

  return (
    <form onSubmit={handleSubmit}>
      <h2>Dodaj rezerwację</h2>
      <div>
        <label>Client ID:</label>
        <input type="number" name="clientId" value={form.clientId} onChange={handleChange} required />
      </div>
      <div>
        <label>Start Date:</label>
        <input type="datetime-local" name="startDate" value={form.startDate} onChange={handleChange} required />
      </div>
      <div>
        <label>End Date:</label>
        <input type="datetime-local" name="endDate" value={form.endDate} onChange={handleChange} required />
      </div>
      <div>
        <label>Promotion ID (opcjonalnie):</label>
        <input type="number" name="promotionId" value={form.promotionId} onChange={handleChange} />
      </div>
      <button type="submit">Zapisz</button>
    </form>
  );
};

export default AddReservationForm;
