import React, { useState } from 'react';
import axios from 'axios';
import './ReservationDetails.css';

const ReservationDetails = () => {
  const [reservationId, setReservationId] = useState('');
  const [reservation, setReservation] = useState(null);
  const [error, setError] = useState('');

  const fetchReservation = () => {
    if (!reservationId) return;

    axios.get(`http://localhost:7074/reservations/${reservationId}`)
      .then(res => {
        setReservation(res.data);
        setError('');
      })
      .catch(err => {
        console.error('Błąd:', err);
        setReservation(null);
        setError('Nie znaleziono rezerwacji.');
      });
  };

  return (
    <div className="reservation-details-container">
      <h2>Sprawdź Rezerwację</h2>

      <div className="input-group">
        <input
          type="number"
          placeholder="Wpisz ID rezerwacji"
          value={reservationId}
          onChange={(e) => setReservationId(e.target.value)}
        />
        <button onClick={fetchReservation}>Szukaj</button>
      </div>

      {error && <p className="error">{error}</p>}

      {reservation && (
        <div className="reservation-card">
          <p><strong>ID:</strong> {reservation.id}</p>
          <p><strong>Klient:</strong> {reservation.clientId}</p>
          <p><strong>Od:</strong> {new Date(reservation.startDate).toLocaleString()}</p>
          <p><strong>Do:</strong> {new Date(reservation.endDate).toLocaleString()}</p>
          <p><strong>Status:</strong> {translateStatus(reservation.status)}</p>
          <p><strong>Promocja:</strong> {reservation.promotionCode || 'brak'} ({reservation.discountPercentage || 0}%)</p>
        </div>
      )}
    </div>
  );
};

const translateStatus = (status) => {
  switch (status) {
    case 0: return 'Oczekująca';
    case 1: return 'Potwierdzona';
    case 2: return 'Anulowana';
    default: return status;
  }
};

export default ReservationDetails;
