import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './ReservationList.css';

const ReservationList = () => {
  const [reservations, setReservations] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    axios.get('http://localhost:7074/reservations')
      .then(res => {
        setReservations(res.data);
        setLoading(false);
      })
      .catch(err => {
        console.error('Błąd pobierania rezerwacji:', err);
        setLoading(false);
      });
  }, []);

  if (loading) return <p>Ładowanie danych...</p>;

  return (
    <div>
      <h1>Lista Rezerwacji</h1>
      {reservations.length === 0 ? (
        <p>Brak rezerwacji.</p>
      ) : (
        <ul>
          {reservations.map(r => (
            <li key={r.id}>
              <strong>Klient ID:</strong> {r.clientId}<br />
              <strong>Od:</strong> {new Date(r.startDate).toLocaleString()}<br />
              <strong>Do:</strong> {new Date(r.endDate).toLocaleString()}<br />
              <strong>Status:</strong> {translateStatus(r.status)}<br />
              <strong>Promocja:</strong> {r.promotionCode || 'brak'} ({r.discountPercentage || 0}%)
              <hr />
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

const translateStatus = (status) => {
  switch (status) {
    case '0':
    case 0:
      return 'Oczekująca';
    case '1':
    case 1:
      return 'Potwierdzona';
    case '2':
    case 2:
      return 'Anulowana';
    default:
      return status;
  }
};

export default ReservationList;
