import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';
import './ReservationDetails.css';

const ReservationDetails = () => {
  const { id } = useParams();
  const [reservation, setReservation] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    axios.get(`http://localhost:7074/reservations/${id}`)
      .then(res => {
        setReservation(res.data);
        setLoading(false);
      })
      .catch(err => {
        console.error('Błąd pobierania rezerwacji:', err);
        setLoading(false);
      });
  }, [id]);

  if (loading) return <p>Ładowanie...</p>;
  if (!reservation) return <p>Nie znaleziono rezerwacji.</p>;

  return (
    <div className="reservation-details">
      <h2>Szczegóły rezerwacji</h2>
      <p><strong>ID:</strong> {reservation.id}</p>
      <p><strong>Klient:</strong> {reservation.clientFullName} (ID: {reservation.clientId})</p>
      <p><strong>Email:</strong> {reservation.clientEmail}</p>
      <p><strong>Telefon:</strong> {reservation.clientPhoneNumber}</p>
      <p><strong>Od:</strong> {new Date(reservation.startDate).toLocaleString()}</p>
      <p><strong>Do:</strong> {new Date(reservation.endDate).toLocaleString()}</p>
      <p><strong>Status:</strong> {reservation.status}</p>
      <p><strong>Promocja:</strong> {reservation.promotionCode || 'brak'} ({reservation.discountPercentage || 0}%)</p>
    </div>
  );
};

export default ReservationDetails;
