import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './ReservationList.css';

const ReservationList = () => {
  const [reservations, setReservations] = useState([]);
  const [loading, setLoading] = useState(true);

  const fetchReservations = () => {
    axios.get('http://localhost:7074/reservations')
      .then(res => {
        setReservations(res.data);
        setLoading(false);
      })
      .catch(err => {
        console.error('Błąd pobierania rezerwacji:', err);
        setLoading(false);
      });
  };

  useEffect(() => {
    fetchReservations(); // pierwszy raz

    const interval = setInterval(() => {
      fetchReservations(); // co 10 sekund
    }, 10000);

    return () => clearInterval(interval); // wyczyść na odmontowaniu
  }, []);

  if (loading) return <p>Ładowanie danych...</p>;

  return (
    <div className="reservation-list">
      <h1>Lista Rezerwacji</h1>
      {reservations.length === 0 ? (
        <p>Brak rezerwacji.</p>
      ) : (
        <ul>
          {reservations.map(r => (
            <li key={r.id}>
              <div className="card">
                <p><strong>ID:</strong> {r.id}</p>
                <p><strong>Klient:</strong> {r.clientId}</p>
                <p><strong>Od:</strong> {new Date(r.startDate).toLocaleString()}</p>
                <p><strong>Do:</strong> {new Date(r.endDate).toLocaleString()}</p>
                <p><strong>Status:</strong> {translateStatus(r.status)}</p>
                <p><strong>Promocja:</strong> {r.promotionCode || 'brak'} ({r.discountPercentage || 0}%)</p>
              </div>
            </li>
          ))}
        </ul>
      )}
    </div>
  );
};

const translateStatus = (status) => {
  switch (status) {
    case '0':
    case 0:
      return 'Oczekująca';
    case '1':
    case 1:
      return 'Potwierdzona';
    case '2':
    case 2:
      return 'Anulowana';
    default:
      return status;
  }
};

export default ReservationList;
