import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';
import './ReservationDetails.css';

const ReservationDetails = () => {
  const { id } = useParams();
  const [reservation, setReservation] = useState(null);
  const [notFound, setNotFound] = useState(false);

  useEffect(() => {
    axios.get(`http://localhost:7074/reservations/${id}`)
      .then(res => {
        setReservation(res.data);
        setNotFound(false);
      })
      .catch(err => {
        console.error(err);
        setNotFound(true);
      });
  }, [id]);

  if (notFound) {
    return <p className="not-found">Nie znaleziono rezerwacji.</p>;
  }

  if (!reservation) {
    return <p className="loading">Ładowanie danych rezerwacji...</p>;
  }

  return (
    <div className="reservation-details">
      <h2>Szczegóły Rezerwacji</h2>
      <p><strong>ID:</strong> {reservation.id}</p>
      <p><strong>Status:</strong> {translateStatus(reservation.status)}</p>
      <p><strong>Klient:</strong> {reservation.clientFullName}</p>
      <p><strong>Email:</strong> {reservation.clientEmail}</p>
      <p><strong>Telefon:</strong> {reservation.clientPhoneNumber}</p>
      <p><strong>Od:</strong> {new Date(reservation.startDate).toLocaleString()}</p>
      <p><strong>Do:</strong> {new Date(reservation.endDate).toLocaleString()}</p>
      <p><strong>Promocja:</strong> {reservation.promotionCode || 'brak'} ({reservation.discountPercentage || 0}%)</p>
    </div>
  );
};

const translateStatus = (status) => {
  switch (status) {
    case 'Confirmed':
    case 1: return 'Potwierdzona';
    case 'Cancelled':
    case 2: return 'Anulowana';
    case 'Pending':
    case 0:
    default: return 'Oczekująca';
  }
};

export default ReservationDetails;
