import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './ReservationList.css';

const ReservationList = () => {
  const [reservations, setReservations] = useState([]);
  const [loading, setLoading] = useState(true);

  const fetchReservations = () => {
    axios.get('/reservations')
      .then(res => {
        setReservations(res.data);
        setLoading(false);
      })
      .catch(err => {
        console.error('Błąd pobierania rezerwacji:', err);
        setLoading(false);
      });
  };

  const confirmReservation = (id) => {
    axios.patch(`/reservations/${id}/confirm`)
      .then(() => {
        alert(`Rezerwacja ${id} potwierdzona.`);
        fetchReservations();
      })
      .catch(err => {
        console.error('Błąd potwierdzenia:', err);
        alert('Nie udało się potwierdzić.');
      });
  };

  const cancelReservation = (id) => {
    axios.patch(`/reservations/${id}/cancel`)
      .then(() => {
        alert(`Rezerwacja ${id} anulowana.`);
        fetchReservations();
      })
      .catch(err => {
        console.error('Błąd anulowania:', err);
        alert('Nie udało się anulować.');
      });
  };

  useEffect(() => {
    fetchReservations();
    const interval = setInterval(fetchReservations, 10000);
    return () => clearInterval(interval);
  }, []);

  if (loading) return <p>Ładowanie danych...</p>;

  return (
    <div className="reservation-list">
      <h2>📋 Lista Rezerwacji</h2>
      {reservations.length === 0 ? (
        <p>Brak rezerwacji.</p>
      ) : (
        reservations.map(r => (
          <div key={r.id} className="reservation-card">
            <div className="reservation-header">
              <span>ID: {r.id}</span>
              <span className={`status ${getStatusClass(r.status)}`}>{translateStatus(r.status)}</span>
            </div>
            <div className="reservation-details">
              <p><strong>👤 Klient:</strong> {r.clientId}</p>
              <p><strong>🕒 Od:</strong> {new Date(r.startDate).toLocaleString()}</p>
              <p><strong>🕒 Do:</strong> {new Date(r.endDate).toLocaleString()}</p>
              <p><strong>🎁 Promocja:</strong> {r.promotionCode || 'brak'} ({r.discountPercentage || 0}%)</p>
            </div>
            <div className="reservation-actions">
              <button className="confirm" onClick={() => confirmReservation(r.id)}>Potwierdź</button>
              <button className="cancel" onClick={() => cancelReservation(r.id)}>Anuluj</button>
            </div>
          </div>
        ))
      )}
    </div>
  );
};

const translateStatus = (status) => {
  switch (status) {
    case '0':
    case 0:
      return 'Oczekująca';
    case '1':
    case 1:
      return 'Potwierdzona';
    case '2':
    case 2:
      return 'Anulowana';
    default:
      return status;
  }
};

const getStatusClass = (status) => {
  switch (status) {
    case '1':
    case 1:
      return 'confirmed';
    case '2':
    case 2:
      return 'cancelled';
    default:
      return 'pending';
  }
};

export default ReservationList;
